sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	//获取选中的service Loc 显示的值
	var u_service_loc=g_form.getValue("u_service_loc");
	// 	g_form.addInfoMessage(">>>>>"+u_service_loc);
	// GlideAjax 的用法 https://developer.servicenow.com/app.do#!/api_doc?v=jakarta&id=c_GlideAjaxAPI
	//script Include 中的name
	var ga=new GlideAjax("hacn");
	//方法名
	ga.addParam("sysparm_name","getCmnLocation");
	//参数
	ga.addParam("sysparm_u_service_loc",u_service_loc);
	//回调
	ga.getXML(getAddress);

}

function getAddress(response){
	var streetAndDistrict=response.responseXML.documentElement.getAttribute("answer");
	if(streetAndDistrict==null){
		return ;
	}
// 	g_form.addInfoMessage(">>>>>>"+streetAndDistrict);
	g_form.setValue("u_address",streetAndDistrict.split("@")[0]);
	g_form.setValue("u_district",streetAndDistrict.split("@")[1]);
}
