function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	g_form.getReference("u_assignto",function(group){
		var assignTo=group.name.replace(/\s/g,"").toLowerCase();
		if(assignTo=="CN-Service Desk".replace(/\s/g,"").toLowerCase())	{
			var emails="Hu Fengquan (SOG CN)<fengquan.hu@sogservice.com.cn>; ricky.huang@sogservice.com<ricky.huang@sogservice.com>; Fu guang (SOGCN)<guang.fu@sogservice.com.cn>; ims<ims@sogservice.com.cn>; Tan Jie(SOG CN)<jie.tan@sogservice.com.cn>";
			cnServiceDesk(emails);
		}
		if(assignTo=="HK IT Support Team".replace(/\s/g,"").toLowerCase()){
			var emails="ithelpdesk@hangseng.com<ithelpdesk@hangseng.com>";
			HKITSupportTeam(emails);
		}

	});


	// 	HK IT Support Team
	//Type appropriate comment here, and begin script below

}


