sys_script_include.script:
var hacn = Class.create();
hacn.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getCmnLocation:function(){
		var u_service_loc=this.getParameter("sysparm_u_service_loc");
		//创建数据库连接
		var glideRecord=new GlideRecord('cmn_location');
		//查询条件，cmn_location 中的 name字段等于选中的u_service_loc
		glideRecord.addQuery("name",u_service_loc);
		//执行查询,并回调recResponse方法
		glideRecord.query();
		//判断是否有数据
		if(glideRecord.next()){
			//从cmn_location表中获取street字段的值 为address字段赋值
			return glideRecord.street+"@"+glideRecord.x_sogd_hacn_district;
			//不能return  glideRecord
		}
		return null;
	},
	type: 'hacn'
});
